pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./interfaces/IVotes.sol";

contract ERC721Votes is ERC721, IVotes {
    error InvalidTokenOwner();

    address public _minter;
    uint256 public nextTokenId;

    mapping(uint256 tokenId => uint256) private _values;
    mapping(uint256 tokenId => uint256) private _campaignJoineds;
    mapping(address user => uint256[] tokenIds) private _tokensOwned;

    constructor(
        string memory name_,
        string memory symbol_,
        address minter_
    ) ERC721(name_, symbol_) {
        _minter = minter_;
    }

    modifier onlyMinter() {
        require(_msgSender() == _minter);
        _;
    }

    function mint(
        address to,
        uint256 value,
        uint256 campaign
    ) external onlyMinter returns (uint256 tokenId) {
        tokenId = nextTokenId;
        nextTokenId++;
        _mint(to, tokenId);
        _values[tokenId] = value;
        _tokensOwned[to].push(tokenId);
    }

    function getVotes(
        uint256 tokenId,
        address account
    ) public view returns (uint256) {
        if (_ownerOf(tokenId) != account) {
            revert InvalidTokenOwner();
        }
        return _values[tokenId];
    }

    function getAllToken(
        address owner
    ) external view returns (TokenInfos[] memory) {
        uint256[] memory tokenOwned = _tokensOwned[owner];
        TokenInfos[] memory tokenInfos = new TokenInfos[](tokenOwned.length);
        for (uint256 i; i < tokenOwned.length; i++) {
            tokenInfos[i] = TokenInfos(
                tokenOwned[i],
                _campaignJoineds[tokenOwned[i]],
                _values[tokenOwned[i]]
            );
        }
        return tokenInfos;
    }

    function tokensOwned(
        address _owner
    ) external view returns (uint256[] memory) {
        return _tokensOwned[_owner];
    }

    function campaignJoineds(uint256 _tokenId) external view returns (uint256) {
        return _campaignJoineds[_tokenId];
    }

    function getVotingPower(uint256 tokenId) external view returns (uint256) {
        return _values[tokenId];
    }
}
","keccak256":"0xe8404aa63a34456ac7312b4eb655dc3569e6be74b8a44e11bb6e55d655cf7924","license":"MIT"},"contracts/Governor.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Context.sol";
import {SafeCast} from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "./interfaces/IGovernor.sol";
import "./interfaces/ICampaign.sol";
import "./interfaces/IRevenuePoolFactory.sol";
import "./RevenuePoolFactory.sol";
import "./GovernorVotes.sol";

contract Governor is Context, IGovernor, GovernorVotes {
    bytes32 private constant ALL_PROPOSAL_STATES_BITMAP =
        bytes32((2 ** (uint8(type(ProposalState).max) + 1)) - 1);
    string private _name;
    bytes32 private _descriptionHash;
    uint256 private _governorId;
    address private _founder;

    ICampaign private _campaign;
    IRevenuePoolFactory private _revenuePoolFactory;
    uint256 private _nextTokenId;
    uint256 private _proposalCounter;
    mapping(uint256 proposalIndex => uint256) private _proposalIds;
    mapping(uint256 proposalId => ProposalCore) private _proposals;
    mapping(uint256 proposalId => Action) private _actions;
    mapping(bytes32 operationHash => bool) private _queuedOperations;

    uint256 private _totalFunded;

    uint64 public votingDelay;
    uint64 public votingPeriod;

    uint64 public timelockPeriod;
    uint64 public queuingPeriod;

    constructor(
        string memory name_,
        string memory tokenName_,
        string memory tokenSymbol_,
        bytes32 descriptionHash_,
        uint256 governorId_,
        address founder_,
        address campaign_,
        uint64 timelockPeriod_,
        uint64 queuingPeriod_
    ) GovernorVotes(tokenName_, tokenSymbol_, campaign_) {
        _name = name_;

        _descriptionHash = descriptionHash_;

        _governorId = governorId_;
        _founder = founder_;
        _campaign = ICampaign(campaign_);

        timelockPeriod = timelockPeriod_;
        queuingPeriod = queuingPeriod_;
    }

    modifier onlyFounder() {
        require(_msgSender() == founder());
        _;
    }

    modifier onlyCampaign() {
        require(_msgSender() == address(_campaign));
        _;
    }

    function setRevenuePoolFactory(address revenuePoolFactory_) external {
        _revenuePoolFactory = IRevenuePoolFactory(revenuePoolFactory_);
    }

    function increaseFundedAndMinted(
        uint256 fundedAmount,
        uint256 mintedAmount
    ) external onlyCampaign {
        _totalFunded += fundedAmount;
        _nextTokenId += mintedAmount;
    }

    function propose(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash,
        uint64 startTime,
        uint64 votingDuration
    ) external onlyFounder returns (uint256) {
        address proposer = _msgSender();
        return
            _propose(
                targets,
                values,
                calldatas,
                descriptionHash,
                proposer,
                startTime,
                votingDuration
            );
    }

    function castVote(
        uint256 proposalId,
        uint256 tokenId,
        uint8 support
    ) external returns (uint256) {
        address voter = _msgSender();
        return _castVote(proposalId, tokenId, voter, support);
    }

    function castVoteBatch(
        uint256 proposalId,
        uint256[] memory tokenIds,
        uint8 support
    ) external returns (uint256 totalWeight) {
        address voter = _msgSender();
        for (uint256 i; i < tokenIds.length; i++) {
            totalWeight += _castVote(proposalId, tokenIds[i], voter, support);
        }
    }

    function queue(uint256 proposalId) external {
        _validateStateBitmap(
            proposalId,
            _encodeStateBitmap(ProposalState.Succeeded)
        );
        ProposalCore storage proposal = _proposals[proposalId];
        Action storage action = _actions[proposalId];
        uint64 etaBlocks = SafeCast.toUint64(clock() + timelockPeriod);
        for (uint256 i = 0; i < action.targets.length; i++) {
            _queueOperation(
                action.targets[i],
                action.values[i],
                action.signatures[i],
                action.calldatas[i],
                proposal.descriptionHash
            );
        }
        proposal.etaBlocks = etaBlocks;
        emit ProposalQueued(proposalId, etaBlocks);
    }

    function execute(uint256 proposalId) public payable {
        _validateStateBitmap(
            proposalId,
            _encodeStateBitmap(ProposalState.Queued)
        );

        ProposalCore storage proposal = _proposals[proposalId];
        // uint256 eta = proposal.etaBlocks;
        // require(
        //     clock() >= eta && clock() <= (eta + queuingPeriod),
        //     "Governor::execute: Transaction can not be executed now."
        // );
        Action storage action = _actions[proposalId];

        for (uint256 i = 0; i < action.targets.length; i++) {
            _executeOperation(
                action.targets[i],
                action.values[i],
                action.signatures[i],
                action.calldatas[i],
                proposal.descriptionHash
            );
        }
        proposal.executed = true;

        emit ProposalExecuted(proposalId);
    }

    function cancel(uint256 proposalId) external {
        _validateStateBitmap(
            proposalId,
            ALL_PROPOSAL_STATES_BITMAP ^
                _encodeStateBitmap(ProposalState.Canceled) ^
                _encodeStateBitmap(ProposalState.Expired) ^
                _encodeStateBitmap(ProposalState.Executed)
        );

        ProposalCore storage proposal = _proposals[proposalId];
        proposal.canceled = true;
        Action storage action = _actions[proposalId];

        for (uint256 i = 0; i < action.targets.length; i++) {
            _cancelOperation(
                action.targets[i],
                action.values[i],
                action.signatures[i],
                action.calldatas[i],
                proposal.descriptionHash
            );
        }

        emit ProposalCanceled(proposalId);
    }

    function hashProposal(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) public pure returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encode(targets, values, calldatas, descriptionHash)
                )
            );
    }

    function hashOperation(
        address target,
        uint256 value,
        string memory signature,
        bytes memory data,
        bytes32 salt
    ) public pure returns (bytes32) {
        return keccak256(abi.encode(target, value, signature, data, salt));
    }

    // ========= VIEW FUNCTIONS =========

    function clock() public view returns (uint256) {
        return block.timestamp;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function founder() public view returns (address) {
        return _founder;
    }

    function governorId() public view returns (uint256) {
        return _governorId;
    }

    function campaign() public view returns (ICampaign) {
        return _campaign;
    }

    function revenuePoolFactory() external view returns (IRevenuePoolFactory) {
        return _revenuePoolFactory;
    }

    function nextTokenId() external view returns (uint256) {
        return _nextTokenId;
    }

    function totalFunded() external view returns (uint256) {
        return _totalFunded;
    }

    function proposalCounter() external view returns (uint256) {
        return _proposalCounter;
    }

    function descriptionHash() external view returns (bytes32) {
        return _descriptionHash;
    }

    function proposalIds(
        uint256 proposalIndex
    ) external view returns (uint256) {
        return _proposalIds[proposalIndex];
    }

    function state(
        uint256 proposalId
    ) public view virtual returns (ProposalState) {
        // We read the struct fields into the stack at once so Solidity emits a single SLOAD
        ProposalCore storage proposal = _proposals[proposalId];
        bool proposalExecuted = proposal.executed;
        bool proposalCanceled = proposal.canceled;

        if (proposalExecuted) {
            return ProposalState.Executed;
        }

        if (proposalCanceled) {
            return ProposalState.Canceled;
        }

        uint256 snapshot = proposalSnapshot(proposalId);

        if (snapshot == 0) {
            revert GovernorNonexistentProposal(proposalId);
        }

        uint256 currentTimepoint = clock();

        if (snapshot >= currentTimepoint) {
            return ProposalState.Pending;
        }

        uint256 deadline = proposalDeadline(proposalId);

        if (deadline >= currentTimepoint) {
            return ProposalState.Active;
        } else if (!_voteSucceeded(proposalId)) {
            return ProposalState.Defeated;
        } else if (proposalEta(proposalId) == 0) {
            return ProposalState.Succeeded;
        } else {
            return ProposalState.Queued;
        }
    }

    function proposalCore(
        uint256 proposalId
    ) external view returns (ProposalCore memory) {
        return _proposals[proposalId];
    }

    function proposalSnapshot(
        uint256 proposalId
    ) public view virtual returns (uint256) {
        return _proposals[proposalId].voteStart;
    }

    function proposalDeadline(
        uint256 proposalId
    ) public view virtual returns (uint256) {
        return
            _proposals[proposalId].voteStart +
            _proposals[proposalId].voteDuration;
    }

    function proposalProposer(
        uint256 proposalId
    ) public view virtual returns (address) {
        return _proposals[proposalId].proposer;
    }

    function proposalEta(
        uint256 proposalId
    ) public view virtual returns (uint256) {
        return _proposals[proposalId].etaBlocks;
    }

    function proposalNeedsQueuing(uint256) public view virtual returns (bool) {
        return false;
    }

    // ========= INTERNAL FUNCTIONS =========
    function _propose(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash,
        address proposer,
        uint64 startTime,
        uint64 votingDuration
    ) internal virtual returns (uint256 proposalId) {
        proposalId = hashProposal(targets, values, calldatas, descriptionHash);

        if (
            targets.length != values.length ||
            targets.length != calldatas.length ||
            targets.length == 0
        ) {
            revert GovernorInvalidProposalLength(
                targets.length,
                calldatas.length,
                values.length
            );
        }
        if (_proposals[proposalId].voteStart != 0) {
            revert GovernorUnexpectedProposalState(
                proposalId,
                state(proposalId),
                bytes32(0)
            );
        }

        _proposalIds[_proposalCounter] = proposalId;
        _proposalCounter += 1;

        /// proposal
        ProposalCore storage proposal = _proposals[proposalId];
        proposal.proposer = proposer;
        proposal.voteStart = startTime;
        proposal.voteDuration = votingDuration;
        proposal.descriptionHash = descriptionHash;

        // action
        Action storage action = _actions[proposalId];
        action.targets = targets;
        action.values = values;
        action.calldatas = calldatas;

        emit ProposalCreated(
            proposalId,
            proposer,
            targets,
            values,
            new string[](targets.length),
            calldatas,
            startTime,
            startTime + votingDuration,
            descriptionHash
        );

        // Using a named return variable to avoid stack too deep errors
    }

    function _castVote(
        uint256 proposalId,
        uint256 tokenId,
        address account,
        uint8 support
    ) internal virtual returns (uint256) {
        _validateStateBitmap(
            proposalId,
            _encodeStateBitmap(ProposalState.Active)
        );
        require(tokenId < _nextTokenId);
        uint256 weight = _getVotes(tokenId, account);
        _countVote(proposalId, tokenId, support, weight);

        emit VoteCast(account, proposalId, tokenId, support, weight);
        return weight;
    }

    function _queueOperation(
        address target,
        uint256 value,
        string memory signature,
        bytes memory data,
        bytes32 descriptionHash
    ) internal {
        bytes32 operationHash = hashOperation(
            target,
            value,
            signature,
            data,
            descriptionHash
        );
        require(
            !_queuedOperations[operationHash],
            "Governor::_queueOperation: Operation has been queued."
        );

        _queuedOperations[operationHash] = true;
    }

    function _executeOperation(
        address target,
        uint256 value,
        string memory signature,
        bytes memory data,
        bytes32 descriptionHash
    ) internal {
        bytes32 operationHash = hashOperation(
            target,
            value,
            signature,
            data,
            descriptionHash
        );
        require(
            _queuedOperations[operationHash],
            "Governor::_executeOperation: Operation hasn't been queued."
        );
        _queuedOperations[operationHash] = false;
        bytes memory callData;
        if (bytes(signature).length == 0) {
            callData = data;
        } else {
            callData = abi.encodePacked(
                bytes4(keccak256(bytes(signature))),
                data
            );
        }
        // bytes memory returnData
        (bool success, ) = target.call{value: value}(callData);
        require(
            success,
            "Governor::_executeOperation: Transaction execution reverted."
        );
    }

    function _cancelOperation(
        address target,
        uint256 value,
        string memory signature,
        bytes memory data,
        bytes32 descriptionHash
    ) internal {
        bytes32 operationHash = hashOperation(
            target,
            value,
            signature,
            data,
            descriptionHash
        );

        _queuedOperations[operationHash] = false;
    }

    function _encodeStateBitmap(
        ProposalState proposalState
    ) internal pure returns (bytes32) {
        return bytes32(1 << uint8(proposalState));
    }

    function _validateStateBitmap(
        uint256 proposalId,
        bytes32 allowedStates
    ) private view returns (ProposalState) {
        ProposalState currentState = state(proposalId);
        if (_encodeStateBitmap(currentState) & allowedStates == bytes32(0)) {
            revert GovernorUnexpectedProposalState(
                proposalId,
                currentState,
                allowedStates
            );
        }
        return currentState;
    }
}
","keccak256":"0x3d4e31880a3132780ec9a8928f3dd791d849330ce5a569206221b8b02d42c9ce","license":"MIT"},"contracts/GovernorFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/ICampaign.sol";
import "./interfaces/IGovernorFactory.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "./Governor.sol";
import "./RevenuePoolFactoryCreator.sol";
import "./interfaces/IVotes.sol";

contract GovernorFactory is OwnableUpgradeable, IGovernorFactory {
    ICampaign private _campaign;
    RevenuePoolFactoryCreator _revenuePoolFactoryCreator;

    uint256 public nextGovernorId;
    mapping(uint256 governorId => address) _governors;
    mapping(address governor => bool) _hasGovernor;
    mapping(address founder => uint256[]) _founderGovernorIds;

    uint64 public votingDelay;
    uint64 public votingPeriod;
    uint64 public timelockPeriod;
    uint64 public queuingPeriod;

    function initialize(
        address initialOwner_,
        address campaign_,
        address revenuePoolFactoryCreator_,
        uint64 timelockPeriod_,
        uint64 queuingPeriod_
    ) public initializer {
        require(initialOwner_ != address(0), "Invalid address");
        require(campaign_ != address(0), "Invalid address");
        require(revenuePoolFactoryCreator_ != address(0), "Invalid address");

        __Ownable_init(initialOwner_);

        _campaign = ICampaign(campaign_);
        timelockPeriod = timelockPeriod_;
        queuingPeriod = queuingPeriod_;
        _revenuePoolFactoryCreator = RevenuePoolFactoryCreator(
            revenuePoolFactoryCreator_
        );
    }

    function changeRevenuePoolFactoryCreator(
        address revenuePoolFactoryCreator_
    ) external onlyOwner {
        require(revenuePoolFactoryCreator_ != address(0), "Invalid address");
        _revenuePoolFactoryCreator = RevenuePoolFactoryCreator(
            revenuePoolFactoryCreator_
        );
    }

    function changeRevenuePoolFactory(
        address governorAddress_,
        address owner_
    ) external onlyOwner {
        require(governorAddress_ != address(0), "Invalid address");
        require(owner_ != address(0), "Invalid address");

        RevenuePoolFactory revenuePoolFactory = _revenuePoolFactoryCreator
            .createRevenuePoolFactory(owner_, governorAddress_);

        Governor(governorAddress_).setRevenuePoolFactory(
            address(revenuePoolFactory)
        );
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {}

    function createGovernor(
        string memory name,
        string memory tokenName,
        string memory tokenSymbol,
        bytes32 descriptionHash
    ) external payable returns (uint256 governorId) {
        governorId = nextGovernorId;
        nextGovernorId += 1;
        // Gover
        address governorAddress = address(
            new Governor(
                name,
                tokenName,
                tokenSymbol,
                descriptionHash,
                governorId,
                msg.sender,
                address(_campaign),
                timelockPeriod,
                queuingPeriod
            )
        );

        RevenuePoolFactory revenuePoolFactory = _revenuePoolFactoryCreator
            .createRevenuePoolFactory(msg.sender, governorAddress);

        Governor(governorAddress).setRevenuePoolFactory(
            address(revenuePoolFactory)
        );

        _governors[governorId] = governorAddress;
        _hasGovernor[governorAddress] = true;
        _founderGovernorIds[msg.sender].push(governorId);

        emit GovernorCreated(
            governorId,
            governorAddress,
            msg.sender,
            descriptionHash
        );
    }

    function governor(
        uint256 governorId
    ) external view override returns (address) {
        return _governors[governorId];
    }

    function founderGovernorIds(
        address founder
    ) external view returns (uint256[] memory) {
        return _founderGovernorIds[founder];
    }

    function founderGovernorAddresses(
        address founder
    ) external view returns (address[] memory) {
        uint256 arrayLength = _founderGovernorIds[founder].length;

        address[] memory addresses = new address[](arrayLength);

        for (uint256 i; i < arrayLength; i++) {
            addresses[i] = _governors[_founderGovernorIds[founder][i]];
        }

        return addresses;
    }

    function hasGovernor(
        address governorAddress
    ) external view override returns (bool) {
        return _hasGovernor[governorAddress];
    }

    function lastProposal(
        address governorAddress
    ) external view returns (Governor.ProposalCore memory, uint256 proposalId) {
        uint256 counter = Governor(governorAddress).proposalCounter();
        if (counter > 0) {
            uint256 proposalId = Governor(governorAddress).proposalIds(
                counter - 1
            );
            return (
                Governor(governorAddress).proposalCore(proposalId),
                proposalId
            );
        }
    }

    function getAllToken(
        address governorAddress,
        address tokenOwner
    ) external view returns (IVotes.TokenInfos[] memory, uint256 totalValue) {
        IVotes.TokenInfos[] memory tokenInfos = IVotes(
            Governor(governorAddress).token()
        ).getAllToken(tokenOwner);

        for (uint256 i; i < tokenInfos.length; i++) {
            totalValue += tokenInfos[i].value;
        }
        return (tokenInfos, totalValue);
    }
}
","keccak256":"0xf0dcd9723a8ab9aed88e357fcff8432ea2ad704464328e7e70694e8b8c5f6dde","license":"MIT"},"contracts/GovernorVotes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IVotes.sol";
import "./interfaces/IGovernorVotes.sol";
import "./ERC721Votes.sol";

contract GovernorVotes is IGovernorVotes {
    IVotes private immutable _token;
    mapping(uint256 proposalId => ProposalVote) private _proposalVotes;

    constructor(
        string memory tokenName_,
        string memory tokenSymbol_,
        address minter_
    ) {
        _token = IVotes(
            address(new ERC721Votes(tokenName_, tokenSymbol_, minter_))
        );
    }

    function hasVoted(
        uint256 proposalId,
        uint256 tokenId
    ) public view returns (bool) {
        return _proposalVotes[proposalId].hasVoted[tokenId];
    }

    function proposalVotes(
        uint256 proposalId
    )
        public
        view
        returns (uint256 againstVotes, uint256 forVotes, uint256 abstainVotes)
    {
        ProposalVote storage proposalVote = _proposalVotes[proposalId];
        return (
            proposalVote.againstVotes,
            proposalVote.forVotes,
            proposalVote.abstainVotes
        );
    }

    function token() public view virtual returns (IVotes) {
        return _token;
    }

    // ======== INTERNAL FUNCTIONS ========

    function _countVote(
        uint256 proposalId,
        uint256 tokenId,
        uint8 support,
        uint256 totalWeight
    ) internal returns (uint256) {
        ProposalVote storage proposalVote = _proposalVotes[proposalId];

        if (proposalVote.hasVoted[tokenId]) {
            revert GovernorAlreadyCastVote(tokenId);
        }
        proposalVote.hasVoted[tokenId] = true;
        if (support == uint8(VoteType.Against)) {
            proposalVote.againstVotes += totalWeight;
        } else if (support == uint8(VoteType.For)) {
            proposalVote.forVotes += totalWeight;
        } else if (support == uint8(VoteType.Abstain)) {
            proposalVote.abstainVotes += totalWeight;
        } else {
            revert GovernorInvalidVoteType();
        }

        return totalWeight;
    }

    function _voteSucceeded(uint256 proposalId) internal view returns (bool) {
        ProposalVote storage proposalVote = _proposalVotes[proposalId];

        return proposalVote.forVotes > proposalVote.againstVotes;
    }

    function _getVotes(
        uint256 tokenId,
        address account
    ) internal view returns (uint256) {
        return token().getVotes(tokenId, account);
    }
}
","keccak256":"0xe1392f03d4d5d33a2f30a710e8f4281c598dcbd4a74bb26c10fd9e9ab9cbbc5e","license":"MIT"},"contracts/Helper.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./interfaces/IRevenuePool.sol";
import "./interfaces/IGovernorVotes.sol";
import "./Governor.sol";
import "./Campaign.sol";
import "./interfaces/IRevenuePoolFactory.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract Helper is OwnableUpgradeable {
    address public campaignContract;

    mapping(address governor => mapping(uint256 tokenId => uint256 amount))
        public claimedAmounts;

    constructor() {
        _disableInitializers();
    }

    function initialize(
        address initialOwner_,
        address campaignContract_
    ) public initializer {
        require(initialOwner_ != address(0), "Invalid address");
        require(campaignContract_ != address(0), "Invalid address");

        __Ownable_init(initialOwner_);

        campaignContract = campaignContract_;
    }

    function setCampaignAddress(address _campaignContract) external onlyOwner {
        require(_campaignContract != address(0), "Invalid address");
        campaignContract = _campaignContract;
    }

    function claimable(
        address governorAddress,
        uint256 tokenId
    ) public view returns (uint256, uint256) {
        uint256 totalAmount;

        IRevenuePoolFactory revenuePoolFactorys = Governor(governorAddress)
            .revenuePoolFactory();

        IRevenuePool[] memory pools = revenuePoolFactorys.pools();

        for (uint256 j; j < pools.length; j++) {
            totalAmount += pools[j].claimable(tokenId);
        }

        return (totalAmount, claimedAmounts[governorAddress][tokenId]);
    }

    function claimables(
        address governorAddress,
        uint256[] calldata tokenIds
    )
        public
        view
        returns (uint256[] memory claimables, uint256[] memory claimeds)
    {
        uint256[] memory claimables = new uint256[](tokenIds.length);
        uint256[] memory claimeds = new uint256[](tokenIds.length);

        for (uint256 i; i < tokenIds.length; i++) {
            (claimables[i], claimeds[i]) = claimable(
                governorAddress,
                tokenIds[i]
            );
        }

        return (claimables, claimeds);
    }

    function claim(address governorAddress, uint256 tokenId) external {
        IRevenuePoolFactory revenuePoolFactorys = Governor(governorAddress)
            .revenuePoolFactory();

        IRevenuePool[] memory pools = revenuePoolFactorys.pools();

        for (uint256 j; j < pools.length; j++) {
            uint256 claimAmount = pools[j].claimable(tokenId);
            if (claimAmount > 0) {
                address tokenAddress = pools[j].token();

                pools[j].claim(tokenId);

                claimedAmounts[governorAddress][tokenId] += claimAmount;
            }
        }
    }
}
