pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./interfaces/IRevenuePool.sol";
import "./interfaces/IGovernor.sol";
import "./interfaces/IGovernorVotes.sol";
import "./interfaces/IRevenuePool.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract RevenuePool is Context, IRevenuePool {
    IGovernorVotes private immutable _governor;
    address private _token;
    uint256 private _revenue;
    uint256 private _totalFunded;
    uint256 private _nextTokenId;
    mapping(uint256 tokenId => bool) private _claimed;
    constructor(
        address governor_,
        address token_,
        uint256 totalFunded_,
        uint256 nextTokenId_,
        uint256 revenue_
    ) payable {
        _governor = IGovernorVotes(governor_);
        _revenue = revenue_;
        _token = token_;
        _totalFunded = totalFunded_;
        _nextTokenId = nextTokenId_;
    }

    function claim(uint256 tokenId) external {
        require(!claimed(tokenId), "Claimed!");
        require(tokenId < _nextTokenId, "Invalid tokenId");

        uint256 value = governor().token().getVotingPower(tokenId);
        address tokenOwner = ERC721(address(governor().token())).ownerOf(
            tokenId
        );

        _claimed[tokenId] = true;

        uint256 claimAmount = (_revenue * value) / _totalFunded;

        ERC20(_token).transfer(tokenOwner, claimAmount);

        emit RevenueClaimed(tokenOwner, tokenId);
    }

    function claimable(uint256 tokenId) public view returns (uint256) {
        if (tokenId < _nextTokenId || !_claimed[tokenId]) {
            uint256 value = governor().token().getVotingPower(tokenId);
            return (_revenue * value) / _totalFunded;
        }
        return 0;
    }

    function claimables(
        uint256[] calldata tokenIds
    ) external view returns (uint256[] memory) {
        uint256[] memory claimableAmounts = new uint256[](tokenIds.length);
        for (uint256 i; i < tokenIds.length; i++) {
            claimableAmounts[i] = claimable(tokenIds[i]);
        }
    }

    function token() public view returns (address) {
        return _token;
    }

    function governor() public view returns (IGovernorVotes) {
        return _governor;
    }
    function revenue() public view returns (uint256) {
        return _revenue;
    }
    function totalFunded() public view returns (uint256) {
        return _totalFunded;
    }

    function nextTokenId() public view returns (uint256) {
        return _nextTokenId;
    }

    function claimed(uint256 tokenId) public view returns (bool) {
        return _claimed[tokenId];
    }
}
","keccak256":"0x7ee054b63b5623671637f67942df325800d72e369eb2aa424c18ba0b6603c1b2","license":"MIT"},"contracts/RevenuePoolFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./interfaces/IGovernor.sol";
import "./interfaces/IRevenuePool.sol";
import "./interfaces/IRevenuePoolFactory.sol";
import "./RevenuePool.sol";

contract RevenuePoolFactory is IRevenuePoolFactory {
    address private _owner;
    IGovernor private immutable _governor;
    IRevenuePool[] private _pools;

    constructor(address owner_, address governor_) {
        _owner = owner_;
        _governor = IGovernor(governor_);
    }

    modifier onlyOwner() {
        require(msg.sender == _owner);
        _;
    }

    function createPool(address token, uint256 amount) external payable {
        RevenuePool revenuePool = new RevenuePool(
            address(_governor),
            token,
            _governor.totalFunded(),
            _governor.nextTokenId(),
            amount
        );

        ERC20(token).transferFrom(msg.sender, address(revenuePool), amount);

        _pools.push(IRevenuePool(address(revenuePool)));

        emit PoolCreated(address(revenuePool));
    }

    function pool(uint256 poolIndex) public view returns (IRevenuePool) {
        return _pools[poolIndex];
    }

    function pools() public view returns (IRevenuePool[] memory) {
        return _pools;
    }

    function poolCounter() external view returns (uint256) {
        return _pools.length;
    }
}
","keccak256":"0x85291ac8567cc074642ec43220788259deb169c0e584071d0d25e44a02febe49","license":"MIT"},"contracts/RevenuePoolFactoryCreator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./RevenuePoolFactory.sol";

contract RevenuePoolFactoryCreator {
    constructor() {}

    function createRevenuePoolFactory(
        address owner_,
        address governor_
    ) external returns (RevenuePoolFactory) {
        RevenuePoolFactory revenuePoolFactory = new RevenuePoolFactory(
            owner_,
            governor_
        );

        return revenuePoolFactory;
    }
}
","keccak256":"0x7ea785ead09d8d08c63a73df8d0f2a8cad68d7c04cbad5ac6220037d14380173","license":"MIT"},"contracts/interfaces/ICampaign.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
interface ICampaign {
    enum CampaignState {
        Pending,
        Active,
        Succeeded,
        Allocated
    }

    event CampaignLaunched(uint256 campaignId);

    event GovernorJoined(uint256 campaignId, uint256 governorId);

    event Fund(
        uint256 campaignId,
        uint256 governorId,
        uint256 amount,
        uint256 tokenId
    );

    event FundAllocated(uint256 campaignId, uint256[] governorIds);

    struct Course {
        address governor;
        uint256 fund;
        uint256 minted;
        bytes32 descriptionHash;
    }

    struct CampaignCore {
        uint256 totalFunded;
        bytes32 descriptionHash;
        uint64 fundStart;
        uint64 fundDuration;
        bool allocated;
        address tokenRaising;
        uint256[] governorIds;
        mapping(uint256 governorId => Course) courses;
    }

    function launchCampaign(
        uint64 startFunding,
        uint64 duration,
        address tokenRaising,
        bytes32 descriptionHash
    ) external returns (uint256 campaignId);

    function joinCampaign(
        uint256 governorId,
        address governor,
        bytes32 descriptionHash
    ) external returns (uint256);

    function fund(
        uint256 campaignId,
        uint256 governorId,
        uint256 amount
    ) external returns (uint256 tokenId);

    function allocateFunds(uint256 campaignId) external;

    function campaignData(
        uint256 campaignId
    )
        external
        view
        returns (
            uint256 totalFunded,
            bytes32 descriptionHash,
            uint64 fundStart,
            uint64 fundDuration,
            bool allocated,
            address tokenRaising,
            uint256[] memory governorIds
        );
}
","keccak256":"0x9850af248ea75b0ab6ff16bf238dd6d1356ef4d03dac8e6fd15d4a910fd90990","license":"MIT"},"contracts/interfaces/IGovernor.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./ICampaign.sol";
import "./IRevenuePoolFactory.sol";
interface IGovernor {
    enum ProposalState {
        Pending,
        Active,
        Canceled,
        Defeated,
        Succeeded,
        Queued,
        Expired,
        Executed
    }

    error GovernorInvalidProposalLength(
        uint256 targets,
        uint256 calldatas,
        uint256 values
    );

    error GovernorUnexpectedProposalState(
        uint256 proposalId,
        ProposalState current,
        bytes32 expectedStates
    );

    error GovernorDisabledDeposit();

    error GovernorOnlyProposer(address account);

    error GovernorOnlyExecutor(address account);

    error GovernorNonexistentProposal(uint256 proposalId);

    event ProposalCreated(
        uint256 proposalId,
        address proposer,
        address[] targets,
        uint256[] values,
        string[] signatures,
        bytes[] calldatas,
        uint256 voteStart,
        uint256 voteEnd,
        bytes32 descriptionHash
    );

    event ProposalQueued(uint256 proposalId, uint256 etaBlocks);

    event ProposalExecuted(uint256 proposalId);

    event ProposalCanceled(uint256 proposalId);

    event VoteCast(
        address indexed voter,
        uint256 proposalId,
        uint256 tokenId,
        uint8 support,
        uint256 weight
    );

    struct ProposalCore {
        address proposer;
        uint64 voteStart;
        uint64 voteDuration;
        bytes32 descriptionHash;
        bool executed;
        bool canceled;
        uint64 etaBlocks;
    }

    struct Action {
        address[] targets;
        uint256[] values;
        string[] signatures;
        bytes[] calldatas;
    }

    function propose(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash,
        uint64 startTime,
        uint64 votingDuration
    ) external returns (uint256 proposalId);

    function castVote(
        uint256 proposalId,
        uint256 tokenId,
        uint8 support
    ) external returns (uint256 weight);

    function castVoteBatch(
        uint256 proposalId,
        uint256[] memory tokenIds,
        uint8 support
    ) external returns (uint256 totalWeight);

    function queue(uint256 proposalId) external;

    function execute(uint256 proposalId) external payable;

    function cancel(uint256 proposalId) external;

    function hashProposal(
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas,
        bytes32 descriptionHash
    ) external pure returns (uint256);

    function hashOperation(
        address target,
        uint256 value,
        string memory signature,
        bytes memory data,
        bytes32 salt
    ) external pure returns (bytes32);

